
public class Loopstate {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/**
		 * Loop Statments:
		 *          while
		 *          do while
		 *          for 
		 *          advanced for(for each)**/
		
		
		// while
		/**
		 * while(Condition)
		 * {
		 *  statement;
		 *  }
		 *  
		 *  **/
		
		
		// do while 
		
		/**
		 * do
		 * {
		 * stmts;
		 * }
		 * while(condition);**/
		
	
		
		int i=5;
		do 
		{
			//System.out.println(i); //op5
			System.out.println(++i); //5 i=6
			System.out.println(i);
		}
		while(i<=8);
		

	}

}
