
public class Stringbypavan {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String name= "Prudhvi";
		String lastname=new String("raj");
		// String is predefined class
		//it is not a primitive datatype
		//String is a collect of characters
		
		
		System.out.println(name.charAt(name.length()-1));
		System.out.println(name.compareTo(lastname));
		System.out.println(name.concat(lastname));
		System.out.println(name.length());
		System.out.println(name.replace('P', 'p'));
		

	}

}
